mapper.select:
SELECT id, post_id, user_id, content, parent_id, created_at, updated_at
FROM comment
WHERE post_id = #{postId}
ORDER BY created_at ASC;

SELECT id, post_id, user_id, content, parent_id, created_at, updated_at
FROM comment
WHERE parent_id = #{parentId}
ORDER BY created_at ASC;

mapper.update:
UPDATE comment
SET content = #{content}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id};
