mapper.select:
SELECT 

FROM user_diet_records
WHERE user_id = #{userId} 
AND meal_time BETWEEN #{startTime} AND #{endTime}
AND deleted = 0
ORDER BY meal_time DESC

SELECT 

FROM user_diet_records
WHERE user_id = #{userId} 
AND meal_type = #{mealType}
AND deleted = 0
ORDER BY meal_time DESC

SELECT *
FROM user_diet_record
WHERE user_id = #{userId}
  AND meal_time BETWEEN #{startTime} AND #{endTime}
ORDER BY meal_time DESC

SELECT *
FROM user_diet_record
WHERE user_id = #{userId}
  AND DATE(meal_time) = #{date}
ORDER BY meal_time DESC

SELECT *
FROM user_diet_record
WHERE user_id = #{userId}
  AND meal_type = #{mealType}
ORDER BY meal_time DESC
